import React, {useState, useEffect} from 'react';
import {CSSTransition} from 'react-transition-group';
import './../styles/accordion.scss';

export default function AccordionPanel({status, title, description, institution}) {
    const [isOpen, setIsOpen] = useState(false);
    const projectColor = '#e0f1f1';
    const professionalColor = '#e5e0f1';

    return (
        <div className="accordion" 
            onClick={() => setIsOpen(!isOpen)}>
            <div className="acctitle" style={isOpen ? {backgroundColor: '#b5dddd'} : null}>
                {title}
            </div>
            <CSSTransition
            in={isOpen}
            classNames="menuEvents"
            timeout={300}
            unmountOnExit
            >
            <div className="accdescription">
                {description}
            </div>
            </CSSTransition>
            <div className="accinstitution">
            {institution}
            </div>
        </div>
    )
}
