import React, {useState, useEffect} from 'react';
//import {CSSTransition} from 'react-transition-group';
import { useScrollPosition } from '@n8tb1t/use-scroll-position';
import AnimateHeight from 'react-animate-height';
import './../styles/accordion.scss';

export default function AccordionPanel({title, description, institution}) {
    // const title = '';
    // const description = 'Description';
    // const institution = 'Target Solutions'
    const [isOpen, setIsOpen] = useState(false);

    return (
        <div className="accordion">
            <div className="acctitle"
                onClick={() => setIsOpen(!isOpen)}>
                {title}
            </div>
            <AnimateHeight
                duration={500}
                delay={100}
                height={0}
                animationStateClasses={animatingDown}
                className="accdescription" 
            > {description}
                {/* <div className="accdescription">
                    {description}
                </div> */}
            </AnimateHeight>
            <div className="accinstitution">
                {institution}
            </div>
        </div>
    )
}
