import React, {useState, useEffect} from 'react';
import { TransitionGroup } from 'react-transition-group';
import useCurrentWidth from './functions.js';
import './styles/header.scss';

const MenuItems = () => {
    return (
        <div className="nav">
            <a href="/">Home</a>
            <a href="/">Sobre</a>
            <a href="/">Tutoriais</a>
            <a href="/">Jogar</a>
        </div>
    )
};

export default function Header() {
    let windowWidth = useCurrentWidth();
    const [expandedMenu, setExpandedMenu] = useState(false);

    function handleMenu() {
        if (expandedMenu) {
            expandedMenu = false;
        } else {
            expandedMenu = true
        }
    }

    return(
        <div className="headerNav">
            <div className="logo">
                over
                <span className="subLogo">aligned</span>
                <span className="subLogoCode">();</span>
            </div>

            <div className="nav">
                {windowWidth > 700
                ? <MenuItems />
                : <div className="plus" onClick={handleMenu()}>
                    menu({expandedMenu ? 'close' : 'open'});
                </div>
                }
            </div>
        </div>
    );
}
