import React, {useState, useEffect} from 'react';
import useCurrentWidth from './functions.js';
import './styles/header.css';

const MenuItems = () => {
    return(
        <>
            <a href="/">Home</a>
            <a href="/">Sobre</a>
            <a href="/">Tutoriais</a>
            <a href="/">Jogar</a>
        </>
    )
};

export default function Header() {
    let windowWidth = useCurrentWidth();

    return(
        <div>
            {windowWidth < 650 
            ? (<b style={{color: "red"}}>this window is small</b>)
            : (<b style={{color: "blue"}}>this window is large</b>)
            }
        </div>
    );
}
