import React, {useState, useEffect} from 'react';
//import {CSSTransition} from 'react-transition-group';
import './../styles/accordion.scss';

export default function AccordionPanel({status, title, type, description, institution}) {
    const [isOpen, setIsOpen] = useState(false);
    const projectColor = '#e0f1f1';
    const professionalColor = '#e5e0f1';

    return (
        <div className="accordion" 
            onClick={() => setIsOpen(!isOpen)}>
            <div className="acctitle" style={isOpen ? {backgroundColor: '#f0f7de'} : null}>
                {title} <span className="viewStatus">[{isOpen ? '-' : '+'}]</span>
            </div>
            {isOpen &&
            (<div className="accdescription">
                {description}
            </div>)
            }
            <div className="accinstitution">
                {institution}
            </div>
        </div>
    )
}
