import React, {useState, useEffect} from 'react';
import { TransitionGroup } from 'react-transition-group';
import useCurrentWidth from './functions.js';
import './styles/header.scss';

const MenuItems = () => {
    return(
        <>
            <a href="/">Home</a>
            <a href="/">Sobre</a>
            <a href="/">Tutoriais</a>
            <a href="/">Jogar</a>
        </>
    )
};

export default function Header() {
    let windowWidth = useCurrentWidth();
    const [visibleButton, setVisibleButton] = useState(false);

    return(
        <div className="headerNav">
            <div className="logo">over<span className="subLogo">aligned</span></div>
            {windowWidth > 650 
            ? (<b style={{color: "blue"}}>this window is large</b>)
            : (<button
                onClick={() => setVisibleButton(true)}
                >Click Here</button>)
            }
        </div>
    );
}
