import React, {useState, useEffect} from 'react';

const Modal = ({children}) => {
    const [show, setShow] = useState(false);

    return (
        <div className="modal">
            {children}

            <button onclick="() => setShow(false)">close</button>
        </div>
    );

}

export default Modal;
