import React, {useState, useEffect} from 'react';
import {Transition} from 'react-transition-group';
import useCurrentWidth from './functions.js';
import './styles/header.scss';


const MenuItems = () => {
    return (
        <>
            <a href="/">Home</a>
            <a href="/">Sobre</a>
            <a href="/">Etc</a>
        </>
    )
};

export default function Header() {
    let windowWidth = useCurrentWidth();
    var [expandedMenu, setExpandedMenu] = useState(false);

    function handleMenu() {
        (expandedMenu) ? setExpandedMenu(false) : setExpandedMenu(true);
    }

    return(
        <>

        <TransitionGroup
            in={expandedMenu}
            classNames="menuEvents"
            timeout={200}
        >
            <div class="verticalMenu">
                teste
            </div>
        </TransitionGroup>

        <div className="headerNav">
            <div className="logo">
                over
                <span className="subLogo">aligned</span>
                <span className="subLogoCode">();</span>
            </div>

            <div className="nav">
                {windowWidth > 900
                ? <MenuItems />
                :<>
                <div className="plus" 
                    onClick={() => setExpandedMenu(!expandedMenu)}
                >
                    [{expandedMenu ? '-' : '+'}]
                </div>
                </>
                }
            </div>
        </div>

        {/* {expandedMenu && (
            <div class="verticalMenu">
                teste
            </div>
        )} */}

        </>
    );
}
