import React, {useState, useEffect} from 'react';
//import {CSSTransition} from 'react-transition-group';
import './../styles/accordion.scss';

export default function AccordionPanel({status, type, title, description, institution}) {
    const [isOpen, setIsOpen] = useState(false);
    const projectColor = '#e0f1f1';
    const professionalColor = '#e5e0f1';

    return (
        <div className="accordion" >
            <div className="acctitle" style={isOpen ? {backgroundColor: '#b5dddd'} : null}
                 onClick={() => setIsOpen(!isOpen)}
            >
                {title}
            </div>
            {isOpen &&
            (<div className="accdescription"
                  onClick={() => setIsOpen(!isOpen)}>
                {description}
            </div>)
            }
            {type.toLowerCase().includes('article') && (
            <a href="http://google.com" target="_blank">
                <div className="additional" style={isOpen ? {borderTop: '2px'} : {borderTop: '0px'}}>
                GO TO ARTICLE
                </div>
            </a>
            )}
            <div className="accinstitution" onClick={() => setIsOpen(!isOpen)}>
            {institution}
            </div>
        </div>
    )
}
