import React, {useState, useEffect, useRef} from 'react';
import { useScrollPosition } from '@n8tb1t/use-scroll-position';
import useCurrentWidth from '../utils/width';
import {CSSTransition} from 'react-transition-group';
import './../styles/header.scss';

const Logo = () => {
    return (
        <div className="logo">
            <a href="/">
            over
            <span className="subLogo">aligned</span>
            </a>
        </div>
    )
};

const MenuItems = () => {
    return (
        <>
            <a href="/">about</a>
            <a href="#projects">projects</a>
            <a href="/">contact</a>
        </>
    )
};

export default function Header() {
    var windowWidth = useCurrentWidth();
    var windowHeight = null;
    const menu = useRef(0);
    var [expandedMenu, setExpandedMenu] = useState(false);
    var [headerBg, setHeaderBg] = useState(false);

    useEffect(() => {
      if (expandedMenu) {
        document.addEventListener('mousedown', onClickMenu);
      }
    }, [expandedMenu]);

    useScrollPosition(({ currPos }) => {
        windowHeight = Math.abs(currPos.y);
        if (windowHeight >= 75) {
            setExpandedMenu(false);
        }
        if (windowHeight >= 350) {
            setHeaderBg(true);
        } else {
            setHeaderBg(false);
        }
        console.log(headerBg);
    });
  
    const onClickMenu = e => {
        document.removeEventListener('mousedown', onClickMenu);
        setExpandedMenu(false);
    };

    const Navigation = () => {
        if (windowWidth > 750) {
            setExpandedMenu(false);
            return <MenuItems />
        } else {
            return (
            <div className="plus"
            onClick={() => setExpandedMenu(!expandedMenu)}
            >
                [{expandedMenu ? '-' : '+'}]
            </div>
            )
        }
    };

    return(
        <>
        <div className="headerNav"
             //style={headerBg ? {position: 'fixed'} : null}
        >
            <Logo />

            <div className="nav">
                <Navigation />
            </div>
        </div>
        <CSSTransition
            in={expandedMenu && windowWidth <= 750}
            classNames="menuEvents"
            timeout={300}
            unmountOnExit
        >
            <div className="verticalMenu" ref={menu}>
                <MenuItems />
            </div>
        </CSSTransition>

        </>
    );
}
