import React, {useState, useEffect} from 'react';
import {CSSTransition} from 'react-transition-group';
import useCurrentWidth from './functions.js';
import './styles/header.scss';


const MenuItems = () => {
    return (
        <>
            <a href="/">Home</a>
            <a href="/">Sobre</a>
            <a href="/">Etc</a>
        </>
    )
};

export default function Header() {
    let windowWidth = useCurrentWidth();
    var [expandedMenu, setExpandedMenu] = useState(false);

    return(
        <>

        <div className="headerNav">
            <div className="logo">
                over
                <span className="subLogo">aligned</span>
                <span className="subLogoCode">();</span>
            </div>

            <div className="nav">
                {windowWidth > 900
                ? <MenuItems />
                :<>
                    <div className="plus" 
                        onClick={() => setExpandedMenu(!expandedMenu)}
                    >
                        [{expandedMenu ? '-' : '+'}]
                    </div>
                </>
                }
            </div>
        </div>

            <CSSTransition
                in={expandedMenu && windowWidth < 900}
                classNames="menuEvents"
                timeout={300}
                unmountOnExit
            >
                <div class="verticalMenu">
                    <MenuItems />
                </div>
            </CSSTransition>

        </>
    );
}
