import React, {useState, useEffect, useRef} from 'react';
import useCurrentWidth from '../utils/width';
import AccordionPanel from '../utils/accordion';
import './../styles/projects.scss';
import ProjectsList from './ProjectsList';
import StackGrid from 'react-stack-grid';

const defaultSearch = ['academic', 'article', 'professional', 'project']

function Projects() {
    const [searchValue, setSearchValue] = useState('');
    const [searchResults, setSearchResults] = useState([]);
    const [clicked, setClicked] = useState(false);
    const grid = React.useRef(null);
    var currentWidth = useCurrentWidth();

    useEffect((e) => {
        const results = ProjectsList.filter(proj => {
            return Object.keys(proj).some(key =>
                proj[key].toLowerCase().includes(searchKey.toLowerCase())
            )
        });
        
        setSearchResults(results);
    }, [searchValue]
    );

    useEffect((e) => {
        requestAnimationFrame(() => {
            if (grid.current) {
              grid.current.updateLayout();
            }
        });
    }, [grid, currentWidth, clicked]
    );

    return (
        <>
            <div className="container">
                <div className="itemspace">
                    <div className="title">Projects</div>

                    <div className="subtitle">seeing {searchResults.length} of {ProjectsList.length}
                    </div>
                    <div className="filterbox">
                        <input 
                            type='text'
                            placeholder='search'
                            value={searchValue}
                            onChange={(e) => setSearchValue(e.target.value)}
                            className="searchField"
                            style={currentWidth <= 700 ? {width: 'calc(80vw - 22px)'} : {width: 'calc(33.33% - 30px)'}}
                        />
                        <button onClick={() => setSearchValue('')}>clear</button>
                        {defaultSearch.map(key => (
                            <button onClick={() => setSearchValue(key)}
                                    style={searchKey==key ? {backgroundColor: '#e0f1f1'} : null}
                            >{key}</button>
                        ))}
                    </div>
                    <div className="projects">
                        <StackGrid 
                            columnWidth={currentWidth <= 600 ? '100%' : '33.33%'}
                            gutterHeight={5}
                            gutterWidth={15}
                            gridRef={r => (grid.current = r)}
                        >
                            {searchResults.map(item => (
                                <div className="click" onClick={() => setClicked(!clicked)}>
                                    <AccordionPanel
                                    title={item.title}
                                    type={item.type}
                                    description={item.description}
                                    institution={item.institution}
                                    />
                                </div>
                            ))}
                        </StackGrid>
                    </div>
                </div>
            </div>
        </>
    )
}

export default Projects;
