import React, {useState, useEffect, useRef} from 'react';
import { useScrollPosition } from '@n8tb1t/use-scroll-position';
import useCurrentWidth from '../utils/width';
import {CSSTransition} from 'react-transition-group';
import './../styles/header.scss';

//import Modal from './Modal.js'
import './../styles/modal.scss';

const Logo = () => {
    return (
        <div className="logo">
            over
            <div className="subLogo1">aligned</div>
            
        </div>
    )
};


const MenuItems = () => {
    const [showAboutModal, setShowAboutModal] = useState(false);
    
    const Dialog = ({children, show}) => {
        return (
            <CSSTransition
                in={showAboutModal}
                classNames="modalEvents"
                timeout={300}
                unmountOnExit
            >
            <div className="modal">
                
                <div className="dialog">
    
                    teste
                    <button onClick={() => setShowAboutModal(false)}>close</button>
    
                </div>
                
            </div>
            </CSSTransition>
        );
    }

    function handleShowModal() {
        setShowAboutModal(true);
        setTimeout(setShowAboutModal(false), 500);
        console.log(showAboutModal);
    }
    
    return (
        <>
            <div className="navBut" onClick={() => handleShowModal()}>about</div>
            <Dialog show={showAboutModal}/>
            <a href="#projects">projects</a>
            <a href="/">contact</a>

        </>
    )
};

export default function Header() {
    var windowWidth = useCurrentWidth();
    var windowHeight = null;
    var [expandedMenu, setExpandedMenu] = useState(false);
    var [showMenuOnScroll, setShowMenuOnScroll] = useState(false);
    const menu = useRef(0);
    const maxWidth = 750;

    useEffect(() => {
      if (expandedMenu) {
        document.addEventListener('mousedown', onClickMenu);
      }
    }, [expandedMenu]);

    useScrollPosition(({ currPos }) => {
        windowHeight = Math.abs(currPos.y);
        if (windowHeight >= 75) {
            setExpandedMenu(false);
        }
    });
  
    const onClickMenu = e => {
        document.removeEventListener('mousedown', onClickMenu);
        setExpandedMenu(false);
    };

    const Navigation = () => {
        if (windowWidth > maxWidth) {
            return <MenuItems />
        } else if (windowWidth <= maxWidth) {
            return (
            <div className="plus"
                onClick={() => setExpandedMenu(!expandedMenu)}
            >
                [{expandedMenu ? '-' : '+'}]
            </div>
            )
        }
    };

    return(
        <>
        <div className="headerNav">
            <Logo />

            <div className="nav">
                <Navigation />
            </div>
        </div>
        <CSSTransition
            in={expandedMenu && windowWidth <= maxWidth}
            classNames="menuEvents"
            timeout={300}
            unmountOnExit
        >
            <div className="verticalMenu" ref={menu}>
                <MenuItems />
            </div>
        </CSSTransition>        

        </>
    );
}
