import React, {useState, useEffect, useRef} from 'react';
import {CSSTransition} from 'react-transition-group';
import AccordionPanel from './../functions/accordion';
import AutoResponsive from 'autoresponsive-react';
import './../styles/projects.scss';
import useCurrentWidth from '../functions/width';

const ProjectsList = [
    {
        title: 'Tuffão Baja SAE',
        type: 'project',
        institution: 'Universidade Federal Fluminense',
        keywords: 'universidade federal fluminense college university engineering baja electronics'
    },
    {
        title: 'Mechanics Tutor',
        type: 'project',
        institution: 'Universidade Federal Fluminense',
        keywords: 'universidade federal fluminense college university engineering mechanics'
    },
    {
        title: 'Internal Audit Intern',
        type: 'internship',
        institution: 'Nissan Motor Corporation',
        keywords: 'nissan motor corporation internal audit intern internship'
    },
    {
        title: 'Tax Technologies Intern',
        institution: 'TechnipFMC',
        type: 'internship',
        keywords: 'technipfmc tax technologies intern internship'
    },
    {
        title: 'Development and Automated Tests Intern',
        institution: 'Target Solutions',
        type: 'intership',
        keywords: 'development automated frontend tests intern internship'
    }
];

function Projects() {
    const [searchValue, setSearchValue] = useState('');
    const [searchResults, setSearchResults] = useState([]);
    const [searchProfessional, setSearchProfessional] = useState(0);

    useEffect((e) => {
        const results = ProjectsList.filter(proj => {
            return Object.keys(proj).some(key =>
                proj[key].toLowerCase().includes(searchValue.toLowerCase())
            )
        });
        setSearchResults(results);
    }, [searchValue]
    );

    function getAutoResponsiveProps() {
        var width = useCurrentWidth();
        return {
          itemMargin: 10,
          containerWidth: width,
          itemClassName: 'item',
          gridWidth: 100,
          transitionDuration: '.5'
        };
      }

    return (
        <>
            <div className="container">
                <div className="itemspace">
                    <div className="title">Projects</div>

                    <div className="subtitle">a compilation of articles, designs, and others</div>
                    <div className="filterbox">
                        <input 
                            type='text'
                            placeholder='search'
                            value={searchValue}
                            onChange={(e) => setSearchValue(e.target.value)}
                            className="searchField"
                        />

                    </div>
                    <AutoResponsive ref="container">
                    {searchResults.map(item => (
                        <AccordionPanel
                            title={item.title}
                            description={item.keywords}
                            institution={item.institution}
                        />
                    ))}
                    </AutoResponsive>
                </div>
            </div>
        </>
    )
}

export default Projects;
