import React, {useState, useEffect, useRef} from 'react';
import useCurrentWidth from './../functions/width';
import { useScrollPosition } from '@n8tb1t/use-scroll-position';
import AccordionPanel from './../functions/accordion';
import './../styles/projects.scss';
import ProjectsList from './ProjectsList';
import StackGrid from 'react-stack-grid';

function Projects() {
    const [searchValue, setSearchValue] = useState('');
    const [searchResults, setSearchResults] = useState([]);
    const [clicked, setClicked] = useState(false);
    const grid = React.useRef(null);
    var scrollPos = 0;
    var currentWidth = useCurrentWidth();

    useScrollPosition(({ currPos }) => {
        scrollPos = Math.abs(currPos.y);
    });

    useEffect((e) => {
        const results = ProjectsList.filter(proj => {
            return Object.keys(proj).some(key =>
                proj[key].toLowerCase().includes(searchValue.toLowerCase())
            )
        });
        requestAnimationFrame(() => {
            if (grid.current) {
              grid.current.updateLayout();
              //console.log(grid.current);
            }
          });
        setSearchResults(results);
    }, [searchValue, grid, currentWidth, clicked, scrollPos]
    );

    return (
        <>
            <div className="container">
                <div className="itemspace">
                    <div className="title">Projects</div>

                    <div className="subtitle">seeing {searchResults.length} of {ProjectsList.length} </div>
                    <div className="filterbox">
                        <input 
                            type='text'
                            placeholder='search'
                            value={searchValue}
                            onChange={(e) => setSearchValue(e.target.value)}
                            className="searchField"
                        />

                    </div>
                    <div className="projects">
                        <StackGrid 
                            columnWidth={currentWidth <= 900 ? '50%' : '33.33%'}
                            gutterHeight={5}
                            gutterWidth={15}
                            gridRef={r => (grid.current = r)}
                        >
                            {searchResults.map(item => (
                                <div className="click" onClick={() => setClicked(!clicked)}>
                                    <AccordionPanel
                                    title={item.title}
                                    type={item.type}
                                    description={item.description}
                                    institution={item.institution}
                                    />
                                </div>
                            ))}
                        </StackGrid>
                    </div>
                </div>
            </div>
        </>
    )
}

export default Projects;
