import React, {useState, useEffect, useRef} from 'react';
import {CSSTransition} from 'react-transition-group';
import useCurrentWidth from './functions.js';
import './styles/header.scss';

const Logo = () => {
    return (
        <div className="logo">
        over
        <span className="subLogo">aligned</span>
        <span className="subLogoCode">();</span>
    </div>
    )
};

const MenuItems = () => {
    return (
        <>
            <a href="/">Home</a>
            <a href="/">Sobre</a>
            <a href="/">Etc</a>
        </>
    )
};

export default function Header() {
    let windowWidth = useCurrentWidth();
    const node = useRef();
    var [expandedMenu, setExpandedMenu] = useState(false);

    useEffect(() => {
        document.addEventListener("mousedown", handleClick);
        return () => {
          document.removeEventListener("mousedown", handleClick);
        };
      }, []);

    const handleClick = e => {
        // if (node.current.contains(e.target)) {
        //     // inside click
        //     return;
        // }
        setExpandedMenu(false);
    };

    return(
        <>

        <div className="headerNav">
            <Logo />

            <div className="nav">
                {windowWidth > 900
                ? <MenuItems />
                :<>
                    <div className="plus" 
                        onClick={() => setExpandedMenu(!expandedMenu)}
                    >
                        [{expandedMenu ? '-' : '+'}]
                    </div>
                </>
                }
            </div>
        </div>

        <CSSTransition
            in={expandedMenu && windowWidth < 900}
            classNames="menuEvents"
            timeout={300}
            unmountOnExit
        >
            <div class="verticalMenu" ref="{node}">
                <MenuItems />
            </div>
        </CSSTransition>

        </>
    );
}
