import React, {useState, useEffect, useRef} from 'react';
import {CSSTransition} from 'react-transition-group';
import useCurrentWidth from './functions.js';
import './styles/header.scss';

const Logo = () => {
    return (
        <div className="logo">
        over
        <span className="subLogo">aligned</span>
        {/* <span className="subLogoCode">();</span> */}
    </div>
    )
};

const MenuItems = () => {
    return (
        <>
            <a href="/">Home</a>
            <a href="/">Sobre</a>
            <a href="/">Etc</a>
        </>
    )
};

export default function Header() {
    let windowWidth = useCurrentWidth();
    const menu = useRef(null);
    var [expandedMenu, setExpandedMenu] = useState(false);

    useEffect(() => {
      if (expandedMenu) {
        document.addEventListener('mousedown', onClickMenu);
      }
    }, [expandedMenu]);
  
    const onClickMenu = e => {
      if (menu.current.contains(e.target)) {
        setExpandedMenu(true);
        return;
      }
      document.removeEventListener('mousedown', onClickMenu);
      setExpandedMenu(false);
    };

    const Navigation = () => {
        if (windowWidth > 900) {
            return <MenuItems />
        } else {
            return (
            <div className="plus"
            onClick={() => setExpandedMenu(!expandedMenu)}
            >
                [{expandedMenu ? '-' : '+'}]
            </div>
            )
        }
    };

    return(
        <>

        <div className="headerNav">
            <Logo />

            <div className="nav">
                <Navigation />
            </div>
        </div>

        <CSSTransition
            in={expandedMenu && windowWidth < 900}
            classNames="menuEvents"
            timeout={300}
            unmountOnExit
        >
            <div className="verticalMenu" ref={menu}>
                <MenuItems />
            </div>
        </CSSTransition>

        </>
    );
}
