import React, {useState, useEffect, useRef} from 'react';
//import {CSSTransition} from 'react-transition-group';
import AccordionPanel from './../functions/accordion';
import './../styles/projects.scss';

const ProjectsList = [
    {
        title: 'Tuffão Baja SAE',
        type: 'project',
        institution: 'Universidade Federal Fluminense',
        description: 'Participação no projeto de engenharia Baja SAE, dentro do subsistema de Eletroeletrônica e núcleo de aquisição de dados, realizando a programação de sensores utilizando o microcontrolador Arduino e a interface dos dados com o piloto.'
    },
    {
        title: 'Mechanics Tutor',
        type: 'project',
        institution: 'Universidade Federal Fluminense',
        description: 'Avaliar e propor ajustes para melhoria do processo ensino-aprendizagem; colaborar em atividades de aprendizado com resolução de exercícios; auxiliar os alunos nos trabalhos de pesquisa.'
    },
    {
        title: 'Internal Audit Intern',
        type: 'internship',
        institution: 'Nissan Motor Corporation',
        description: 'Formalização de requisições/ordens de compra no sistema SAP (ERP); publicação de informações e documentos na intranet do GRC; preparação de apresentações para reporte do status do plano de auditoria para fins de comitês de auditoria e sessões de monitoramento de desempenho do GRC; monitoramento e atualização do status dos issues identificados pela auditoria interna e respectivos planos de ação para fins de reporte ao ponto focal global do “Issue Follow-up”; apoio na execução dos procedimentos de avaliação dos controles internos para atendimento das exigências da J-SOX; acompanhamento e documentação dos procedimentos de auditoria adotados no acompanhamento dos sorteios e campanhas internas no âmbito da Nissan Brasil.'
    },
    {
        title: 'Tax Technologies Intern',
        type: 'internship',
        institution: 'TechnipFMC',
        description: 'Suporte aos usuários nos sistemas fiscais; atualização dos sistemas disponíveis; realização da interface entre usuários dos sistemas e TI; configuração de sistemas.'
    },
    {
        title: 'Development and Automated Tests Intern',
        type: 'internship',
        institution: 'Target Solutions',
        description: 'Atualização do caderno de testes com base nas especificações de tela e funcionalidades; realização de testes manuais; realização de testes automatizados utilizando Robot Framework e Selenium; desenvolvimento de telas em front-end e correção de bugs.'
    }
];

function Projects() {
    const [searchValue, setSearchValue] = useState('');
    const [searchResults, setSearchResults] = useState([]);

    useEffect((e) => {
        const results = ProjectsList.filter(proj => {
            return Object.keys(proj).some(key =>
                proj[key].toLowerCase().includes(searchValue.toLowerCase())
            )
        });
        setSearchResults(results);
    }, [searchValue]
    );

    return (
        <>
            <div className="container">
                <div className="itemspace">
                    <div className="title">Projects</div>

                    <div className="subtitle">seeing {searchResults.length} of {ProjectsList.length} </div>
                    <div className="filterbox">
                        <input 
                            type='text'
                            placeholder='search'
                            value={searchValue}
                            onChange={(e) => setSearchValue(e.target.value)}
                            className="searchField"
                        />

                    </div>
                    <div className="projects">
                    {searchResults.map(item => (
                        <AccordionPanel
                            title={item.title}
                            description={item.description}
                            institution={item.institution}
                        />
                    ))}
                    </div>
                </div>
            </div>
        </>
    )
}

export default Projects;
